import React, {Component} from 'react';

shelfMapping = {};


class Books extends Component {



    render () {
        return (
            <div className="book">
                <div className="book-top">
                <div className="book-cover" style={{ width: 128, height: 174, backgroundImage: `url( ${this.props.bookDetail.bookurl} )` }}></div>
                <div className="book-shelf-changer">
                    <select value={this.props.shelf}>
                        <option value="move" disabled>Move to...</option>
                        <option value="currentlyReading">Currently Reading</option>
                        <option value="wantToRead">Want to Read</option>
                        <option value="read">Read</option>
                        <option value="none">None</option>
                    </select>
                </div>
                </div>
                <div className="book-title">{this.props.bookDetail.booktitle}</div>
                <div className="book-authors">{this.props.bookDetail.bookauthors}</div>
            </div>
        )
    }
}

export default Books;
