import React, {Component} from 'react';

class Books extends Component {
    handleChange = (e) => {
        //e.preventDefault()
        //const values = serializeForm(e.target, {hash: true})
        if (this.props.changingShelf) {
            this.props.changingShelf(e.target.value, this.props.bookDetail);
        }
    }

    render () {
        const { bookDetail } = this.props;

        return (
            <li>
                <div className="book">
                    <div className="book-top">
                        <div className="book-cover" style={{ width: 128, height: 174, backgroundImage: `url( ${bookDetail.imageLinks.thumbnail} )` }}></div>
                        <div className="book-shelf-changer">
                            <select value={bookDetail.shelf} onChange={this.handleChange}>
                                <option value="move" disabled>Move to...</option>
                                <option value="currentlyReading">Currently Reading</option>
                                <option value="wantToRead">Want to Read</option>
                                <option value="read">Read</option>
                                <option value="none">None</option>
                            </select>
                        </div>
                    </div>
                    <div className="book-title">{bookDetail.title}</div>
                    <div className="book-authors">{bookDetail.authors.join(`{<br>}`)}</div>
                </div>
            </li>
        )
    }
}

export default Books;
