import React from 'react';
import * as BooksAPI from './BooksAPI'
import './App.css';
import SearchPage from './components/search';
import Shelf from './components/shelf';

class BooksApp extends React.Component {
  state = {
    myLibrary: [],
    /**
     * TODO: Instead of using this state variable to keep track of which page
     * we're on, use the URL in the browser's address bar. This will ensure that
     * users can use the browser's back and forward buttons to navigate between
     * pages, as well as provide a good URL they can bookmark and share.
     */
    showSearchPage: false
  }

  componentDidMount () {
    BooksAPI.getAll()
      .then((myLibrary) => {
        this.setState(() => ({
          myLibrary
        }))
      })
  }

  onChangingShelf = (newShelf, bookDetails) => {
    //Case 1: Book exists in library and we are changing shelf to something other than none
    //Case 2: Book exist in library and we are changing shelf to none
    //Case 3: Book does not exist in library and we are adding
    let bookInLibrary = this.state.myLibrary.find((findingBook) => findingBook.id === bookDetails.id);
    if (bookInLibrary) {
      if (newShelf === "none" ) {
        this.setState((currentState) => (
          currentState.myLibrary.pop(bookInLibrary)
        ))
      } else {
        this.setState((currentState) => (
          currentState.myLibrary.find((y) => y.id === bookDetails.id).shelf = newShelf
        ))
      }
    } else {
      bookDetails.shelf = newShelf;
      this.setState((currentState) => ({
        myLibrary: [...currentState, bookDetails]
      }))
    }

  }

  render() {
    return (
      <div className="app">
        {this.state.showSearchPage ? (
          <SearchPage myLibrary={this.state.myLibrary} changingShelf={(newShelf, bookDetails) => (this.onChangingShelf(newShelf, bookDetails))}/>
        ) : (
          <div className="list-books">
            <div className="list-books-title">
              <h1>MyReads</h1>
            </div>
            <div className="list-books-content">
              <div>
                <Shelf myLibrary={this.state.myLibrary} changingShelf={(newShelf, bookDetails) => (this.onChangingShelf(newShelf, bookDetails))}/>
              </div>
            </div>
            <div className="open-search">
              <button onClick={() => this.setState({ showSearchPage: true })}>Add a book</button>
            </div>
          </div>
        )}
      </div>
    )
  }
}

export default BooksApp
