import React from 'react';
// import * as BooksAPI from './BooksAPI'
import './App.css';
import SearchPage from './components/search';
import Shelf from './components/shelf';

class BooksApp extends React.Component {
  componentDidMount () {
    ContactsAPI.getAll()
      .then((contacts) => {
        console.log(contacts)
        //this.setState(() => ({
        //  contacts
        //}))
      })
  }


  state = {
    myLibrary: 
      [ 
        {
          shelfName:"currentlyReading", 
          books: [
            {booktitle: "To Kill a Mockingbird", bookauthors: "Harper Lee", bookurl: "http://books.google.com/books/content?id=PGR2AwAAQBAJ&printsec=frontcover&img=1&zoom=1&imgtk=AFLRE73-GnPVEyb7MOCxDzOYF1PTQRuf6nCss9LMNOSWBpxBrz8Pm2_mFtWMMg_Y1dx92HT7cUoQBeSWjs3oEztBVhUeDFQX6-tWlWz1-feexS0mlJPjotcwFqAg6hBYDXuK_bkyHD-y&source=gbs_api"},
            {booktitle: "To Kill a Mockingbird2", bookauthors: "Harper Lee", bookurl: "http://books.google.com/books/content?id=PGR2AwAAQBAJ&printsec=frontcover&img=1&zoom=1&imgtk=AFLRE73-GnPVEyb7MOCxDzOYF1PTQRuf6nCss9LMNOSWBpxBrz8Pm2_mFtWMMg_Y1dx92HT7cUoQBeSWjs3oEztBVhUeDFQX6-tWlWz1-feexS0mlJPjotcwFqAg6hBYDXuK_bkyHD-y&source=gbs_api"}
          ]
        },
        {
          shelfName:"wantToRead", 
          books: [
            {booktitle: "Oh, the Places You'll Go!", bookauthors: "Seuss", bookurl: "http://books.google.com/books/content?id=1q_xAwAAQBAJ&printsec=frontcover&img=1&zoom=1&imgtk=AFLRE712CA0cBYP8VKbEcIVEuFJRdX1k30rjLM29Y-dw_qU1urEZ2cQ42La3Jkw6KmzMmXIoLTr50SWTpw6VOGq1leINsnTdLc_S5a5sn9Hao2t5YT7Ax1RqtQDiPNHIyXP46Rrw3aL8&source=gbs_api"},
            {booktitle: "Oh, the Places You'll Go!2", bookauthors: "Seuss", bookurl: "http://books.google.com/books/content?id=1q_xAwAAQBAJ&printsec=frontcover&img=1&zoom=1&imgtk=AFLRE712CA0cBYP8VKbEcIVEuFJRdX1k30rjLM29Y-dw_qU1urEZ2cQ42La3Jkw6KmzMmXIoLTr50SWTpw6VOGq1leINsnTdLc_S5a5sn9Hao2t5YT7Ax1RqtQDiPNHIyXP46Rrw3aL8&source=gbs_api"}
          ]
        },
        {
          shelfName:"read", 
          books: [
            {booktitle: "To Kill a Mockingbird", bookauthors: "Harper Lee", bookurl: "http://books.google.com/books/content?id=PGR2AwAAQBAJ&printsec=frontcover&img=1&zoom=1&imgtk=AFLRE73-GnPVEyb7MOCxDzOYF1PTQRuf6nCss9LMNOSWBpxBrz8Pm2_mFtWMMg_Y1dx92HT7cUoQBeSWjs3oEztBVhUeDFQX6-tWlWz1-feexS0mlJPjotcwFqAg6hBYDXuK_bkyHD-y&source=gbs_api"}
          ]
        },
    ],
    /**
     * TODO: Instead of using this state variable to keep track of which page
     * we're on, use the URL in the browser's address bar. This will ensure that
     * users can use the browser's back and forward buttons to navigate between
     * pages, as well as provide a good URL they can bookmark and share.
     */
    showSearchPage: false
  }

  onChangingShelf = (newShelf, bookDetails, currentShelf) => {
    //console.log(newShelf);
    //console.log(bookDetails);
    //console.log(currentShelf);
    this.setState((currentState) => (
      currentState.myLibrary.find((y) => (y.shelfName === newShelf)).books.push(currentState.myLibrary.find((x) => (x.shelfName === currentShelf)).books.pop(bookDetails))
      ))
    //this.setState( this.state.myLibrary.find((y) => (y.shelfName === newShelf)).books.push(this.state.myLibrary.find((x) => (x.shelfName === currentShelf)).books.pop(bookDetails)));
    //this.state.myLibrary.find((x) => (x.shelfName === newShelf)).books.push();
  }

  render() {
    return (
      <div className="app">
        {this.state.showSearchPage ? (
          <SearchPage/>
        ) : (
          <div className="list-books">
            <div className="list-books-title">
              <h1>MyReads</h1>
            </div>
            <div className="list-books-content">
              <div>
                <Shelf myLibrary={this.state.myLibrary} changingShelf={(newShelf, bookDetails, currentShelf) => (this.onChangingShelf(newShelf, bookDetails, currentShelf))}/>
              </div>
            </div>
            <div className="open-search">
              <button onClick={() => this.setState({ showSearchPage: true })}>Add a book</button>
            </div>
          </div>
        )}
      </div>
    )
  }
}

export default BooksApp
